import React from 'react';
import axios from 'axios';

// @material-ui/core components
import { makeStyles } from '@material-ui/core/styles';

// @material-ui/icons

// core components
import GridContainer from 'components/Grid/GridContainer.js';
import GridItem from 'components/Grid/GridItem.js';
import CustomInput from 'components/CustomInput/CustomInput.js';
import Button from 'components/CustomButtons/Button.js';

import styles from 'assets/jss/nextjs-material-kit/pages/landingPageSections/workStyle.js';

const useStyles = makeStyles(styles);

export default function WorkSection() {
	const classes = useStyles();

	return (
		<div className={classes.section}>
			<GridContainer justify='center'>
				<GridItem cs={12} sm={12} md={8}>
					<h2 className={classes.title}>Work with us</h2>
					<h4 className={classes.description}>
						We would love to hear from you. Please allow us time to review your
						message and prepare for our conversation. Thank you!
					</h4>
					<form onSubmit={e => this.formSubmit(e)}>
						<GridContainer>
							<GridItem xs={12} sm={12} md={6}>
								<CustomInput
									labelText='Your Name'
									id='name'
									formControlProps={{
										fullWidth: true
									}}
								/>
							</GridItem>
							<GridItem xs={12} sm={12} md={6}>
								<CustomInput
									labelText='Your Email'
									id='email'
									formControlProps={{
										fullWidth: true
									}}
								/>
							</GridItem>
							<CustomInput
								labelText='Your Message'
								id='message'
								formControlProps={{
									fullWidth: true,
									className: classes.textArea
								}}
								inputProps={{
									multiline: true,
									rows: 5
								}}
							/>
							<GridItem xs={12} sm={12} md={4} className={classes.textCenter}>
								<Button color='info'>Send Message</Button>
							</GridItem>
						</GridContainer>
					</form>
				</GridItem>
			</GridContainer>
		</div>
	);
}
